import * as types_mod from "./types";
export class Engine {
    static tick;
    static work;
    static start(work) {
        Engine.tick = 0;
        Engine.work = work;
        Engine.#frame();
    }
    static #id;
    static #frame() {
        Engine.#id = requestAnimationFrame(Engine.#frame);
        Engine.work(Engine.tick++);
    }
    static stop() {
        cancelAnimationFrame(Engine.#id);
    }
}
